create view routes
as
select
	route_name
	,grade
	,area
	,country
	,first_ascenionist
	,first_ascent_date
	,count(*) as number_of_ascents
	,max(ascent_date) as most_recent_ascent
	
from
	ascents
	
group by
	route_name
	,grade
	,area
	,country
	,first_ascenionist
	,first_ascent_date
	
order by
	grade desc
	,first_ascent_date
	,route_name;